import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux/lib/hooks/useSelector';
import { detailsBeat } from '../actions/beatActions';

function BeatStore(props) {

    const beatDetails = useSelector(state => state.beatDetails);
    const {  beat, loading, error } = beatDetails;
    const dispatch = useDispatch;

    useEffect(() => {
        dispatch(detailsBeat());
        return () => {
            //;
        }

    }, [])

    return <div>
        <div className="back-to-home">
            <Link to="/">Home</Link>
        </div>
            {loading? <div>Loading...</div>:
            error? <div>{error} </div>:
            (
                <div className="details">
                <div className="details-image">
                    <img src={beat.image} alt="beat"></img>
                </div>
            <div className="details-info">
                <ul>
                    <li>
                        <h4>{beat.name}</h4>
                    </li>
                    <li>
                       Price: <b>${beat.price}</b>
                    </li>
                    <li>
                       Artist: {beat.artist}
                    </li>
                </ul>
             </div> 
             <div className="details-action">
                 <ul>
                    <li>
                        Price: {beat.price}
                    </li>
                    <li>
                        Years: <select>
                            <option>1</option>
                            <option>2</option>
                            <option>Own</option>
                        </select>
                        </li> 
                        <li>
                            <button className="button primary" >Add to Cart</button>
                        </li>
                     </ul>
                 </div>   
            </div>  
            )
        }
        
    </div>
}

export default BeatStore
