import { BEAT_LIST_REQUEST, BEAT_LIST_SUCCESS, BEAT_LIST_FAIL, BEAT_DETAILS_FAIL, BEAT_DETAILS_SUCCESS, BEAT_DETAILS_REQUEST } from "../constants/beatConstants";

function beatListReducer(state = {products: [] }, action) {
    
    switch (action.type) {
        case BEAT_LIST_REQUEST:
            return {loading: true };
        case BEAT_LIST_SUCCESS:
            return {loading:false, products: action.payload}
        case BEAT_LIST_FAIL:
            return { loading: false, error: action.payload}
        default:
            return state;
    }
}

function beatDetailsReducer(state = {product: {} }, action){
    
    switch (action.type) {
        case BEAT_DETAILS_REQUEST:
            return {loading: true };
        case BEAT_DETAILS_SUCCESS:
            return {loading:false, product: action.payload}
        case BEAT_DETAILS_FAIL:
            return { loading: false, error: action.payload}
        default:
            return state;
    }
}

export { beatListReducer, beatDetailsReducer }
