import React, { useEffect } from 'react';
import { addToCart, removeFromCart } from '../actions/cartActions';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
function CartScreen(props) {

  const cart = useSelector(state => state.cart);

  const { cartItems } = cart;


  const productId = props.match.params.id;
  const lease = props.location.search ? Number(props.location.search.split("=")[1]) : 1;
//  const qty = props.location.search ? Number(props.location.search.split("=")[1]) : 1;
  const dispatch = useDispatch();
  const removeFromCartHandler = (productId) => {
    dispatch(removeFromCart(productId));
  }
  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, lease));
    }
  }, []);

  const checkoutHandler = () => {
    props.history.push("/signin?redirect=billing");
  }

  return <div className="cart">
    <div className="cart-list">
      <ul className="cart-list-container">
        <li>
          <h3>
            Shopping Cart
          </h3>
          <div>
            Price
          </div>
        </li>
        {
          cartItems.length === 0 ?
            <div>
              Cart is empty
          </div>
            :
            cartItems.map(item =>
              <li>
                <div className="cart-image">
                  <img src={item.image} alt="product" />
                </div>
                <div className="cart-name">
                  <div>
                    <Link to={"/product/" + item.product}>
                      {item.name}
                    </Link>

                  </div>
                  <div>
                    Lease:
                  <select value={item.lease} onChange={(e) => dispatch(addToCart(item.product, e.target.value))}>
                      <option>Non Exclusive Basic</option>
                      <option>Non Exclusive Premium</option>
                      <option>Non Exclusive Standard</option>
                      <option>Non Exclusive Pro</option>
                      <option>Exclusive Pro Unlimited</option>
                      <option>Copyright Assignment</option>
                </select> 
                    <button type="button" className="button" onClick={() => removeFromCartHandler(item.product)} >
                      Delete
                    </button>
                  </div>
                </div>
                <div className="cart-price">
                  ${item.price}
                </div>
              </li>
            )
        }
      </ul>

    </div>
    <div className="cart-action">
      <h3>
        Subtotal ( {cartItems.reduce((a, c) => a + c, 0)} items)
        :
         { cartItems.map( item =>
            <div> 
             {item.price} 
            </div>
            )}
      </h3>
      <button onClick={checkoutHandler} className="button primary full-width" disabled={cartItems.length === 0}>
        Proceed to Checkout
      </button>

    </div>

  </div>
}

export default CartScreen;
