import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import CheckoutSteps from '../components/CheckoutSteps';
import { createOrder } from '../actions/orderActions';
function PlaceOrderScreen(props) {

  const cart = useSelector(state => state.cart);
  const orderCreate = useSelector(state => state.orderCreate);
  const { loading, success, error, order } = orderCreate;

  const { cartItems, billing, payment } = cart;
  if (!billing.address) {
    props.history.push("/billing");
  } else if (!payment.paymentMethod) {
    props.history.push("/payment");
  }
  //const itemsPrice = cartItems.reduce((a, c) => a + c.price * c.qty, 0);
  //const billingPrice = itemsPrice > 100 ? 0 : 10;
  const taxPrice = 10;
  const billingPrice = 0;
  var totalPrice = 1;
 // const totalPrice = itemsPrice + billingPrice + taxPrice;
  const dispatch = useDispatch();

  
  const placeOrderHandler = () => {
    // create an order
    dispatch(createOrder({
      orderItems: cartItems, billing, payment, billingPrice,
      taxPrice, totalPrice
    }));
  }
  useEffect(() => {
    if (success) {
      props.history.push("/order/" + order._id);
    }

  }, [success]);

  return <div>
    <CheckoutSteps step1 step2 step3 step4 ></CheckoutSteps>
    <div className="placeorder">
      <div className="placeorder-info">
        <div>
          <h3>
            billing
          </h3>
          <div>
            {cart.billing.address}, {cart.billing.city},
          {cart.billing.postalCode}, {cart.billing.country},
          </div>
        </div>
        <div>
          <h3>Payment</h3>
          <div>
            Payment Method: {cart.payment.paymentMethod}
          </div>
        </div>
        <div>
          <ul className="cart-list-container">
            <li>
              <h3>
                Shopping Cart
          </h3>
              <div>
                Price
          </div>
            </li>
            {
              cartItems.length === 0 ?
                <div>
                  Cart is empty
          </div>
                :
                cartItems.map(item =>
                  <li>
                    <div className="cart-image">
                      <img src={item.image} alt="product" />
                    </div>
                    <div className="cart-name">
                      <div>
                        <Link to={"/product/" + item.product}>
                          {item.name}
                        </Link>

                      </div>
                      <div>
                        Qty: {item.qty}
                      </div>
                    </div>
                    <div className="cart-price">
                      ${item.price}
                    </div>
                  </li>
                )
            }
          </ul>
        </div>

      
      </div>
      <div className="placeorder-action">
        <ul>
          <li>
            <button className="button primary full-width" onClick={placeOrderHandler} >Place Order</button>
          </li>
          <li>
            <h3>Order Summary</h3>
          </li>
          <li>
            <div>Items</div>
             {totalPrice}
          </li>
          
        </ul>



      </div>

    </div>
  </div>

}

export default PlaceOrderScreen;
